mapper.select:

SELECT id, account, password, email, tel, im, status, gmt_created, gmt_modified
FROM user
WHERE account=#{account}


SELECT id, account, password, email, tel, im, status, gmt_created, gmt_modified
FROM user
WHERE email=#{email}


SELECT id, account, password, email, tel, im, status, gmt_created, gmt_modified
FROM user
WHERE account=#{account} AND password=#{password}


SELECT id, account, password, email, tel, im, status, gmt_created, gmt_modified
FROM user
WHERE email=#{email} AND password=#{password}
